mapper.insert:
insert into Review
(
bno
, id
, title
, content
) values (
board_seq.nextval
, #{id}
, #{title}
,
#{content}
)

mapper.update:
update
Review
set
title =
#{title}
, content = #{content}
where
bno = #{bno}
and id = #{id}

mapper.delete:
delete
Review
where
id like
#{id}
and bno = #{bno}

mapper.select:
select count(*)
from like_board
where id = #{id} and bno = #{bno} and prefix = #{prefix}
